// Homework 1.1 and 1.2
const Header = (props) => {
  return (
      <div>
        <h1>{props.course}</h1>
      </div>
  )
}

const Content = (props) => {
  return (
      <div>
        {/* # 1.1
        <p>"{props.part1}" part includes {props.exercises1} exersises.</p>
